import { __decorate } from "tslib";
import { Component, Input } from '@angular/core';
import { MessageService } from '../services/message.service';
import { Client } from '../Modèle/Client';
let TchatComponent = class TchatComponent {
    constructor(client, socket, route) {
        this.client = client;
        this.socket = socket;
        this.route = route;
        this.messages = [];
        this.message = "";
        this.destinataire = "";
        this.name = "";
        this.id = "";
        this.clients = [];
        this.messageService = new MessageService(socket);
        this.getReceiveInformation();
        this.createClient();
        this.getConnected();
        this.getMessage();
    }
    createClient() {
        this.route.queryParams.subscribe(params => {
            this.client.age = params.age;
            this.client.setName(params.username);
        });
        console.log("Clients size :" + this.clients.length);
        this.client = this.getClientByName(this.client.getName(), this.client.getAge());
        if (this.client != undefined) {
            this.messageService.createClient(this.client);
            this.clients.push(this.client);
        }
    }
    async getConnected() {
        while (true) {
            this.socket.emit('NumberClient');
            await new Promise(r => setTimeout(r, 2000));
        }
    }
    async getReceiveInformation() {
        this.getServerMessage();
        this.getNumberUser();
    }
    async getServerMessage() {
        this.socket.on('client', (data) => {
            if (data.id_envoyeur != this.client.id)
                this.messages.push(data.message);
        });
    }
    async getMessage() {
        this.socket.on('sendTo', (message) => {
            this.messages.push(message);
        });
    }
    getNumberUser() {
        this.socket.on('clients', (data) => {
            for (let i = 0; i < data.length; i++) {
                var client = new Client();
                client.setId(data[i].client.id);
                client.setName(data[i].client.name);
                client.setAge(data[i].client.age);
                client.setSocket(data[i].id_socket);
                if (!this.clientAlreadyExist(client)) {
                    this.clients.push(client);
                }
            }
        });
    }
    clientAlreadyExist(client) {
        for (let i = 0; i < this.clients.length; i++) {
            if (this.clients[i].getName() == client.getName() && this.clients[i].getAge() == client.getAge()) {
                return true;
            }
        }
        return false;
    }
    onSubmit() {
        this.kindOfSend();
        this.messages.push(this.message);
        this.message = "";
    }
    kindOfSend() {
        if (this.destinataire == "Broadcast") {
            this.broadcastMessage(this.client.id, this.message);
        }
        else {
            var clientSelect = this.getClientByName(this.destinataire, this.client.getAge());
            this.sendMessageTo(clientSelect, this.message);
        }
    }
    sendMessageTo(destinataire, message) {
        this.messageService.sendMessageTo(destinataire, message);
    }
    broadcastMessage(idclient, message) {
        this.messageService.broadCastMessage(idclient, message);
    }
    getClientByName(name, age) {
        let client = this.clients.find(v => v.name == name);
        return client == undefined ? undefined : client;
    }
    getClient() {
        return this.client;
    }
    ngOnInit() {
        console.log("Parent component initialized");
    }
    ngOnDestroy() {
        console.log("Parent component destroyed");
    }
};
